INSERT INTO "public"."preguntas" ("codigo", "section", "bloque", "area_id", "exclusivo_rol_id", "texto", "tipo", "opciones", "locale", "peso", "escala", "scoring", "created_at", "respuesta_correcta", "dimension", "exclusivo_nivel_id", "dificultad") VALUES
('DCS-A1', 'Cuestionario', 'Adopción', '8', '16', 'En tu rol de Dirección de Compras/Supply, ¿con qué frecuencia utilizas algún asistente de IA (por ejemplo, tipo chatbot) para redactar o mejorar correos a proveedores?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasionalmente", "Frecuente (semanal)", "Muy frecuentemente"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación"]'::jsonb, 2, 1),

('DCS-A2', 'Cuestionario', 'Adopción', '8', '16', '¿Con qué frecuencia revisas, aunque sea de forma general, si tu ERP o herramienta de compras incluye funciones inteligentes (sugerencias, alertas, clasificaciones automáticas)?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasionalmente", "Frecuente (semanal)", "Muy frecuentemente"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación"]'::jsonb, 2, 1),

('DCS-A3', 'Cuestionario', 'Adopción', '8', '16', '¿Con qué frecuencia pides a tu equipo que explore o pruebe nuevas herramientas de IA aplicadas a Compras, aunque sea en pruebas pequeñas?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasionalmente", "Frecuente (semanal)", "Muy frecuentemente"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación"]'::jsonb, 2, 1),

('DCS-A4', 'Cuestionario', 'Adopción', '8', '16', '¿Con qué frecuencia utilizas IA para preparar resúmenes ejecutivos de información de proveedores, por ejemplo historial de desempeño, riesgos e incidencias, para reuniones internas?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasionalmente", "Frecuente (semanal)", "Muy frecuentemente"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Estrategia", "Aplicación"]'::jsonb, 2, 1),

('DCS-A5', 'Cuestionario', 'Adopción', '8', '16', '¿Con qué frecuencia discutes con otros directivos, como Finanzas, Operaciones o TI, oportunidades básicas de aplicar IA en procesos de Compras?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasionalmente", "Frecuente (semanal)", "Muy frecuentemente"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación"]'::jsonb, 2, 1),

('DCS-A6', 'Cuestionario', 'Adopción', '8', '16', '¿Con qué frecuencia planteas preocupaciones éticas o de riesgo, como privacidad, confidencialidad o sesgos, cuando alguien propone usar IA en la relación con proveedores?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasionalmente", "Frecuente (semanal)", "Muy frecuentemente"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Estrategia"]'::jsonb, 2, 1),

