INSERT INTO "public"."preguntas" ("codigo", "section", "bloque", "area_id", "exclusivo_rol_id", "texto", "tipo", "opciones", "locale", "peso", "escala", "scoring", "created_at", "respuesta_correcta", "dimension", "exclusivo_nivel_id", "dificultad") VALUES
('MF-A1', 'Cuestionario', 'Adopción', '5', '20', 'En mi trabajo en finanzas, utilizo herramientas de IA para aclarar conceptos financieros (por ejemplo, que me expliquen en lenguaje simple términos de estados financieros).', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación", "Inversión"]'::jsonb, 4, 1),

('MF-A2', 'Cuestionario', 'Adopción', '5', '20', 'Uso IA para resumir estados financieros o reportes extensos y entender los puntos clave antes de analizarlos a detalle.', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Productividad", "Inversión", "Aplicación"]'::jsonb, 4, 1),

('MF-A3', 'Cuestionario', 'Adopción', '5', '20', 'Uso IA para detectar posibles errores (celdas inconsistentes, fórmulas sospechosas, montos atípicos) en mis hojas de cálculo financieras.', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación"]'::jsonb, 4, 1),

('MF-A4', 'Cuestionario', 'Adopción', '5', '20', 'Utilizo IA o herramientas con IA integrada para priorizar las partidas a revisar en conciliaciones o cierres (por riesgo, monto, frecuencia, etc.).', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación", "Estrategia"]'::jsonb, 4, 1),

('MF-A5', 'Cuestionario', 'Adopción', '5', '20', 'Lidero o co-lidero experimentos de IA en finanzas (pruebas de concepto, pilotos) midiendo impacto en tiempos, errores o insights generados.', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Productividad", "Aplicación"]'::jsonb, 4, 1),

('MF-A6', 'Cuestionario', 'Adopción', '5', '20', 'Utilizo IA para mejorar la redacción de correos o comentarios en reportes financieros (por ejemplo, explicaciones a otras áreas).', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Productividad", "Inversión"]'::jsonb, 4, 1),

