-- CONOCIMIENTO - Rol 20
INSERT INTO "public"."preguntas" ("codigo", "section", "bloque", "area_id", "exclusivo_rol_id", "texto", "tipo", "opciones", "locale", "peso", "escala", "scoring", "created_at", "respuesta_correcta", "dimension", "exclusivo_nivel_id", "dificultad") VALUES
('MF-C1', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál de las siguientes opciones describe mejor qué es la inteligencia artificial aplicada a finanzas?', 'Multiple Choice (una respuesta)', '["Un sistema que reemplaza completamente a los contadores.", "Tecnologías que analizan datos financieros para automatizar tareas y apoyar la toma de decisiones.", "Un tipo de base de datos tradicional para guardar facturas.", "Un software exclusivo para grandes bancos."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Tecnologías que analizan datos financieros para automatizar tareas y apoyar la toma de decisiones.', '["Conocimiento", "Aplicación"]'::jsonb, 4, 1),

('MF-C2', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál de estas es una buena práctica al usar IA para redactar explicaciones de desviaciones presupuestarias?', 'Multiple Choice (una respuesta)', '["Copiar la respuesta sin revisarla.", "Validar la respuesta contra los datos reales y ajustar el texto según el criterio profesional.", "Pedir que la IA invente cifras para que suene más convincente.", "Usar la misma explicación para todos los reportes sin importar el contexto."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Validar la respuesta contra los datos reales y ajustar el texto según el criterio profesional.', '["Conocimiento"]'::jsonb, 4, 1),

('MF-C3', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál es una forma eficiente de usar IA junto con hojas de cálculo en finanzas?', 'Multiple Choice (una respuesta)', '["Pedir a la IA que cree estados financieros sin datos de entrada.", "Exportar datos a una tabla, explicar el contexto a la IA y pedirle ayuda para encontrar patrones o errores.", "Esconder fórmulas para que nadie entienda el archivo.", "Borrar toda la información sensible antes de analizar."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Exportar datos a una tabla, explicar el contexto a la IA y pedirle ayuda para encontrar patrones o errores.', '["Conocimiento"]'::jsonb, 4, 1),

('MF-C4', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál de estas tareas es más adecuada para un sistema de IA de detección de anomalías?', 'Multiple Choice (una respuesta)', '["Calcular manualmente cada saldo día por día.", "Identificar patrones inusuales de pagos a proveedores respecto de periodos anteriores.", "Imprimir reportes sin revisarlos.", "Eliminar registros con montos altos."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Identificar patrones inusuales de pagos a proveedores respecto de periodos anteriores.', '["Conocimiento"]'::jsonb, 4, 1),

('MF-C5', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué se busca al “industrializar” el uso de IA en finanzas?', 'Multiple Choice (una respuesta)', '["Mantener proyectos aislados y experimentales.", "Integrar IA de forma estable en procesos, con gobierno de datos, métricas y responsables claros.", "Usar IA solo cuando hay tiempo libre.", "Evitar medir el impacto para no generar presión."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Integrar IA de forma estable en procesos, con gobierno de datos, métricas y responsables claros.', '["Conocimiento", "Aplicación"]'::jsonb, 4, 1),

('MF-C6', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál es un ejemplo sencillo de uso de IA en el área de cuentas por pagar (AP)?', 'Multiple Choice (una respuesta)', '["Imprimir manualmente todas las facturas para revisarlas.", "Usar IA para extraer datos clave de las facturas y sugerir cuentas contables.", "Guardar las facturas en cajas físicas por año.", "Hacer las conciliaciones solo al final del año."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Usar IA para extraer datos clave de las facturas y sugerir cuentas contables.', '["Conocimiento", "Aplicación"]'::jsonb, 4, 1),

('MF-C7', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué ventaja tiene usar IA para clasificar gastos en categorías contables?', 'Multiple Choice (una respuesta)', '["Evitar completamente la participación del equipo de finanzas.", "Acelerar la clasificación propuesta, que luego puede ser revisada por humanos.", "Eliminar la necesidad de cumplir con normas contables.", "Hacer que las auditorías ya no sean necesarias."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Acelerar la clasificación propuesta, que luego puede ser revisada por humanos.', '["Conocimiento"]'::jsonb, 4, 2),

('MF-C8', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué ventaja clave aporta la IA en el proceso de cierre contable mensual?', 'Multiple Choice (una respuesta)', '["Permite omitir conciliaciones bancarias.", "Ayuda a identificar errores o anomalías antes de cerrar, reduciendo retrabajos.", "Elimina la necesidad de controles internos.", "Sustituye por completo al equipo contable."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Ayuda a identificar errores o anomalías antes de cerrar, reduciendo retrabajos.', '["Conocimiento"]'::jsonb, 4, 2),

('MF-C9', 'Cuestionario', 'Conocimiento', '5', '20', 'En un flujo de trabajo de conciliación automática con IA, ¿qué rol suele tener el analista de finanzas?', 'Multiple Choice (una respuesta)', '["Ignorar todas las alertas generadas.", "Revisar las excepciones que la IA marca como posibles errores y tomar decisiones de corrección.", "Borrar transacciones duplicadas sin verificación.", "Desactivar los controles cuando se acercan los cierres."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Revisar las excepciones que la IA marca como posibles errores y tomar decisiones de corrección.', '["Conocimiento"]'::jsonb, 4, 2),

('MF-C10', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál es un indicador clave para evaluar el impacto de IA en un proceso financiero?', 'Multiple Choice (una respuesta)', '["Cantidad de correos enviados al día.", "Horas de trabajo manual ahorradas y reducción de errores versus el proceso anterior.", "Número de presentaciones en PowerPoint.", "Número de chistes generados por la IA."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Horas de trabajo manual ahorradas y reducción de errores versus el proceso anterior.', '["Conocimiento", "Inversión"]'::jsonb, 4, 2),

('MF-C11', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál es una limitación importante de usar IA generativa (como ChatGPT) en finanzas?', 'Multiple Choice (una respuesta)', '["Siempre es más precisa que los analistas humanos.", "No comete errores y no necesita revisión.", "Puede “inventar” información si no se le dan datos claros y verificados.", "Solo se puede usar fuera del horario laboral."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Puede “inventar” información si no se le dan datos claros y verificados.', '["Conocimiento"]'::jsonb, 4, 2),

('MF-C12', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué significa “detección de anomalías” en datos financieros con IA?', 'Multiple Choice (una respuesta)', '["Eliminar todas las transacciones pequeñas.", "Buscar patrones repetitivos en correos electrónicos.", "Identificar transacciones inusuales que pueden ser errores o posibles fraudes.", "Ordenar las facturas por fecha."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Identificar transacciones inusuales que pueden ser errores o posibles fraudes.', '["Conocimiento", "Aplicación", "Inversión"]'::jsonb, 4, 2),

('MF-C13', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué significa usar IA como “copiloto” en tareas de finanzas?', 'Multiple Choice (una respuesta)', '["Dejar que la IA tome las decisiones finales.", "Usarla como apoyo para sugerir pasos y automatizar partes del trabajo mientras tú mantienes el control.", "Dejar de aprender contabilidad.", "Usarla solo para chatear con colegas."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Usarla como apoyo para sugerir pasos y automatizar partes del trabajo mientras tú mantienes el control.', '["Conocimiento", "Aplicación"]'::jsonb, 4, 3),

('MF-C14', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué es importante al usar IA para proyecciones de flujo de caja?', 'Multiple Choice (una respuesta)', '["Usar siempre el mismo modelo sin importar cambios en el negocio.", "Revisar supuestos, horizonte temporal y sensibilidad a diferentes escenarios.", "Confiar solo en datos de hace más de 5 años.", "No documentar los parámetros utilizados."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Revisar supuestos, horizonte temporal y sensibilidad a diferentes escenarios.', '["Conocimiento"]'::jsonb, 4, 3),

('MF-C15', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué se entiende por “ciclo de mejora continua” en soluciones de IA en finanzas?', 'Multiple Choice (una respuesta)', '["Implementar y nunca volver a revisar el modelo.", "Revisar periódicamente resultados, ajustar parámetros, datos y reglas según la retroalimentación del negocio.", "Cambiar modelos cada día sin validación.", "Desactivar el sistema cuando hay auditoría."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Revisar periódicamente resultados, ajustar parámetros, datos y reglas según la retroalimentación del negocio.', '["Conocimiento"]'::jsonb, 4, 3),

('MF-C16', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál de estas acciones respeta mejor la confidencialidad de la información financiera al usar IA?', 'Multiple Choice (una respuesta)', '["Copiar estados financieros completos en una herramienta pública sin políticas claras.", "Enviar archivos financieros personales al chat de un amigo.", "Seguir las políticas internas de la empresa sobre qué datos se pueden cargar en herramientas de IA.", "Publicar datos financieros en redes sociales y pedir análisis."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Seguir las políticas internas de la empresa sobre qué datos se pueden cargar en herramientas de IA.', '["Conocimiento", "Aplicación", "Estrategia"]'::jsonb, 4, 3),

('MF-C17', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué tipo de dato deberías evitar cargar en una herramienta de IA pública sin protección adicional?', 'Multiple Choice (una respuesta)', '["Tareas genéricas de estudio.", "Información ya publicada en el sitio web de la empresa.", "Datos de clientes con nombre, monto y número de cuenta.", "Explicaciones conceptuales sobre estados financieros."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Datos de clientes con nombre, monto y número de cuenta.', '["Conocimiento"]'::jsonb, 4, 3),

('MF-C18', 'Cuestionario', 'Conocimiento', '5', '20', 'En un proceso de cuentas por cobrar (AR), ¿cómo puede ayudar la IA?', 'Multiple Choice (una respuesta)', '["Inventando pagos que no existen.", "Agrupando clientes según patrones de pago para priorizar la cobranza.", "Eliminando facturas atrasadas del sistema.", "Cambiando las tasas de interés sin autorización."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Agrupando clientes según patrones de pago para priorizar la cobranza.', '["Conocimiento"]'::jsonb, 4, 3),

('MF-C19', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál es un riesgo al integrar IA a procesos de aprobación de pagos?', 'Multiple Choice (una respuesta)', '["Tener más trazabilidad de decisiones.", "Que las reglas y límites de aprobación no se respeten si se automatizan sin buena configuración.", "Reducir el tiempo de proceso.", "Tener menos errores de tipografía."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Que las reglas y límites de aprobación no se respeten si se automatizan sin buena configuración.', '["Conocimiento", "Estrategia"]'::jsonb, 4, 4),

('MF-C20', 'Cuestionario', 'Conocimiento', '5', '20', '¿Por qué es relevante la colaboración con otras áreas (TI, Riesgos, Operaciones) en proyectos de IA de finanzas?', 'Multiple Choice (una respuesta)', '["Para que finanzas pierda control sobre los procesos.", "Porque muchos casos de uso cruzan sistemas, datos y procesos que no son solo de finanzas.", "Porque así se reparte la responsabilidad de posibles fallos.", "Porque lo exige la moda."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Porque muchos casos de uso cruzan sistemas, datos y procesos que no son solo de finanzas.', '["Conocimiento", "Estrategia"]'::jsonb, 4, 4),

('MF-C21', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál de estas tareas es más adecuada para ser apoyada por IA en finanzas?', 'Multiple Choice (una respuesta)', '["Aprobar pagos sin revisión humana.", "Generar borradores de explicaciones de variaciones de gastos a partir de tablas de Excel.", "Firmar cheques en nombre del CFO.", "Decidir cambios contables sin criterios contables."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Generar borradores de explicaciones de variaciones de gastos a partir de tablas de Excel.', '["Conocimiento"]'::jsonb, 4, 4),

('MF-C22', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál de estas preguntas a la IA es más útil para apoyar el análisis financiero?', 'Multiple Choice (una respuesta)', '["Haz mi trabajo por mí.", "Escribe un chiste sobre contabilidad.", "Revisa esta tabla de gastos y sugiere posibles razones de las variaciones por centro de costo.", "Dime cualquier cosa sobre economía."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Revisa esta tabla de gastos y sugiere posibles razones de las variaciones por centro de costo.', '["Conocimiento", "Inversión"]'::jsonb, 4, 4),

('MF-C23', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué es importante al interpretar recomendaciones de IA en análisis de costos?', 'Multiple Choice (una respuesta)', '["Aceptar todo sin cuestionar.", "Revisar si los supuestos tienen sentido y contrastar con otras fuentes.", "Ignorar los datos de entrada.", "Usar siempre los mismos parámetros sin importar el caso."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Revisar si los supuestos tienen sentido y contrastar con otras fuentes.', '["Conocimiento", "Inversión"]'::jsonb, 4, 4),

('MF-C24', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué es “explicabilidad” en modelos de IA aplicados a finanzas?', 'Multiple Choice (una respuesta)', '["La capacidad de la IA para cambiar sus decisiones sin avisar.", "La facilidad para entender por qué el modelo tomó cierta decisión o recomendación.", "La velocidad de procesamiento del modelo.", "La cantidad de datos almacenados."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'La facilidad para entender por qué el modelo tomó cierta decisión o recomendación.', '["Conocimiento", "Aplicación"]'::jsonb, 4, 4),

('MF-C25', 'Cuestionario', 'Conocimiento', '5', '20', '¿Cuál de las siguientes es una aproximación responsable al uso de IA generativa en soportes a decisiones estratégicas financieras?', 'Multiple Choice (una respuesta)', '["Dejar que la IA decida sola el presupuesto anual.", "Usarla para generar escenarios y argumentos, pero validar con modelos financieros estructurados y criterio experto.", "Ignorar los datos y solo usar texto generado por IA.", "Ajustar las respuestas de la IA para que coincidan con lo que la dirección quiere escuchar."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Usarla para generar escenarios y argumentos, pero validar con modelos financieros estructurados y criterio experto.', '["Conocimiento", "Aplicación"]'::jsonb, 4, 5),

('MF-C26', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué describe mejor la diferencia entre automatización “tradicional” (ej. macros) e IA?', 'Multiple Choice (una respuesta)', '["No hay diferencia, son exactamente lo mismo.", "La automatización tradicional sigue reglas fijas; la IA aprende patrones a partir de datos.", "La automatización tradicional solo se usa en marketing.", "La IA no necesita datos para funcionar."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'La automatización tradicional sigue reglas fijas; la IA aprende patrones a partir de datos.', '["Conocimiento"]'::jsonb, 4, 5),

('MF-C27', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué describe mejor el rol del colaborador de finanzas frente a la IA?', 'Multiple Choice (una respuesta)', '["Dejar de pensar y aceptar todo lo que diga la herramienta.", "Ser un “revisor crítico” que valida, corrige y decide cómo usar las salidas de la IA.", "No usar IA porque es solo para TI.", "Compartir datos confidenciales porque “la IA es segura por defecto”."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Ser un “revisor crítico” que valida, corrige y decide cómo usar las salidas de la IA.', '["Conocimiento"]'::jsonb, 4, 5),

('MF-C28', 'Cuestionario', 'Conocimiento', '5', '20', '¿Por qué es relevante entender de dónde vienen los datos usados por la IA en finanzas?', 'Multiple Choice (una respuesta)', '["Para poder modificarlos sin control.", "Porque la calidad y origen de los datos afectan la confiabilidad de los resultados.", "Porque no es importante, la IA corrige los errores automáticamente.", "Porque así se puede ocultar información."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Porque la calidad y origen de los datos afectan la confiabilidad de los resultados.', '["Conocimiento", "Aplicación"]'::jsonb, 4, 5),

('MF-C29', 'Cuestionario', 'Conocimiento', '5', '20', '¿Por qué puede ser útil involucrar a TI y Riesgos al implementar nuevas herramientas de IA en finanzas?', 'Multiple Choice (una respuesta)', '["Para que se encarguen de todo y finanzas no opine.", "Para asegurar temas de seguridad, integración con otros sistemas y cumplimiento de políticas.", "Para hacer el proceso más lento sin razón.", "Para evitar documentar los cambios."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Para asegurar temas de seguridad, integración con otros sistemas y cumplimiento de políticas.', '["Conocimiento", "Aplicación", "Estrategia"]'::jsonb, 4, 5),

('MF-C30', 'Cuestionario', 'Conocimiento', '5', '20', '¿Qué rol puede jugar un “referente de IA en finanzas” a nivel de colaborador?', 'Multiple Choice (una respuesta)', '["Diseñar políticas de IA de toda la compañía sin involucrar a nadie.", "Ser puente entre el equipo de finanzas y especialistas de datos/tecnología, traduciendo necesidades y limitaciones.", "Sustituir al CFO en la definición de la estrategia.", "Certificar modelos sin evaluarlos."]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Ser puente entre el equipo de finanzas y especialistas de datos/tecnología, traduciendo necesidades y limitaciones.', '["Conocimiento"]'::jsonb, 4, 5);
