INSERT INTO "public"."preguntas" ("codigo", "section", "bloque", "area_id", "exclusivo_rol_id", "texto", "tipo", "opciones", "locale", "peso", "escala", "scoring", "created_at", "respuesta_correcta", "dimension", "exclusivo_nivel_id", "dificultad") VALUES
('MO-A1', 'Cuestionario', 'Adopción', '4', '19', 'En tu trabajo diario, ¿con qué frecuencia usas alguna herramienta digital que incluya IA (por ejemplo, recomendaciones automáticas, sistemas que sugieren rutas u horarios, asistentes tipo ChatGPT) para apoyar tus tareas de operaciones?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación"]'::jsonb, 4, 1),

('MO-A2', 'Cuestionario', 'Adopción', '4', '19', '¿Con qué frecuencia buscas aprender, por tu cuenta, qué es la IA y para qué sirve en operaciones (videos, cursos cortos, materiales internos)?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación"]'::jsonb, 4, 1),

('MO-A3', 'Cuestionario', 'Adopción', '4', '19', '¿Con qué frecuencia piensas que la IA podría ayudar a reducir errores o retrabajos en tu área (aunque todavía no la uses)?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación"]'::jsonb, 4, 1),

('MO-A4', 'Cuestionario', 'Adopción', '4', '19', '¿Con qué frecuencia comentas con tus compañeros o supervisor ideas sobre cómo la IA podría ayudar en alguna parte del proceso (recepción, almacén, producción, despacho, etc.)?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación"]'::jsonb, 4, 1),

('MO-A5', 'Cuestionario', 'Adopción', '4', '19', 'Cuando se menciona una nueva herramienta “inteligente” en la organización, ¿con qué frecuencia te ofreces a participar en pruebas piloto o capacitaciones básicas?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación"]'::jsonb, 4, 1),

('MO-A6', 'Cuestionario', 'Adopción', '4', '19', '¿Con qué frecuencia piensas en los posibles riesgos (errores, sesgos, uso de datos sensibles) cuando escuchas hablar de IA aplicada a tu área?', 'Multiple Choice (escala Likert A–E)', '["Nunca", "Rara vez (≤ 1/mes)", "Ocasional (2–3/mes)", "Frecuente (semanal)", "Casi siempre"]', 'MX/LATAM', '8.333333', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', '{"A": 0, "B": 25, "C": 50, "D": 75, "E": 100}', now(), null, '["Aplicación", "Estrategia"]'::jsonb, 4, 1),

