INSERT INTO "public"."preguntas" ("codigo", "section", "bloque", "area_id", "exclusivo_rol_id", "texto", "tipo", "opciones", "locale", "peso", "escala", "scoring", "created_at", "respuesta_correcta", "dimension", "exclusivo_nivel_id", "dificultad") VALUES
('MO-C1', 'Cuestionario', 'Conocimiento', '4', '19', 'En tu trabajo, ¿cuál de las siguientes opciones describe mejor a la inteligencia artificial (IA)?', 'Multiple Choice (una respuesta)', '["Un tipo de archivo de Excel con muchas fórmulas", "Un robot físico que siempre reemplaza a las personas", "Sistemas que usan datos y algoritmos para tomar decisiones o hacer tareas de forma automática", "Cualquier máquina que tenga una pantalla táctil"]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Sistemas que usan datos y algoritmos para tomar decisiones o hacer tareas de forma automática', '["Conocimiento"]'::jsonb, 4, 1),

('MO-C2', 'Cuestionario', 'Conocimiento', '4', '19', '¿Cuál de estos ejemplos es un uso sencillo de IA en operaciones?', 'Multiple Choice (una respuesta)', '["Escribir a mano los reportes de turno en una libreta", "Un sistema que sugiere la mejor ruta de reparto según tráfico y distancia", "Imprimir las órdenes de trabajo en papel", "Guardar archivos en una carpeta compartida"]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Un sistema que sugiere la mejor ruta de reparto según tráfico y distancia', '["Conocimiento", "Aplicación"]'::jsonb, 4, 1),

('MO-C3', 'Cuestionario', 'Conocimiento', '4', '19', '¿Qué beneficio directo podría traer la IA a tu trabajo diario en operaciones?', 'Multiple Choice (una respuesta)', '["Hacer que todos los procesos sean más lentos", "Aumentar la cantidad de papeleo manual", "Reducir errores y retrabajos al detectar problemas antes", "Evitar que el equipo se comunique"]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Reducir errores y retrabajos al detectar problemas antes', '["Conocimiento"]'::jsonb, 4, 1),

('MO-C4', 'Cuestionario', 'Conocimiento', '4', '19', '¿Cuál de estas situaciones representa un riesgo al usar IA?', 'Multiple Choice (una respuesta)', '["Revisar críticamente las sugerencias y compararlas con tu experiencia", "Usar la IA como apoyo, pero siempre validar con datos reales", "Compartir información sensible de clientes o proveedores en una herramienta abierta sin autorización", "Preguntar a la IA cómo resumir un instructivo público"]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Compartir información sensible de clientes o proveedores en una herramienta abierta sin autorización', '["Conocimiento", "Estrategia"]'::jsonb, 4, 1),

('MO-C5', 'Cuestionario', 'Conocimiento', '4', '19', 'Si tu supervisor te pide “probar una herramienta de IA para reportes de turno”, ¿qué actitud es más adecuada?', 'Multiple Choice (una respuesta)', '["Negarse sin conocerla, porque “esas cosas son para TI”", "Probarla con mente abierta, respetando los procedimientos y reportando hallazgos", "Usarla para todo sin seguir procedimientos establecidos", "Compartir tu usuario y contraseña con compañeros para que también prueben"]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Probarla con mente abierta, respetando los procedimientos y reportando hallazgos', '["Conocimiento", "Productividad"]'::jsonb, 4, 1),

('MO-C6', 'Cuestionario', 'Conocimiento', '4', '19', 'Respecto a la inversión de tiempo en aprender IA, ¿qué enunciado es más correcto para un miembro de operaciones?', 'Multiple Choice (una respuesta)', '["No tiene sentido aprender IA; nunca se usará en operaciones", "Solo deben aprender IA los directores y gerentes", "Vale la pena invertir algo de tiempo en entender usos básicos que mejoren mi trabajo", "Solo el área de sistemas debe saber de IA"]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Vale la pena invertir algo de tiempo en entender usos básicos que mejoren mi trabajo', '["Inversión", "Conocimiento", "Productividad"]'::jsonb, 4, 1),

('MO-C7', 'Cuestionario', 'Conocimiento', '4', '19', '¿Cuál es un uso básico y apropiado de IA generativa para un miembro de operaciones?', 'Multiple Choice (una respuesta)', '["Inventar datos de producción cuando faltan registros", "Redactar un correo más claro para informar un retraso en una entrega", "Cambiar las fechas de entrega en el sistema sin avisar a nadie", "Firmar digitalmente un documento en nombre de otra persona"]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Redactar un correo más claro para informar un retraso en una entrega', '["Conocimiento", "Aplicación"]'::jsonb, 4, 2),

('MO-C8', 'Cuestionario', 'Conocimiento', '4', '19', 'Un sistema de IA que analiza lecturas de sensores en una máquina para avisar que “pronto puede fallar” se llama:', 'Multiple Choice (una respuesta)', '["IA creativa", "Mantenimiento preventivo tradicional", "Mantenimiento predictivo", "Análisis manual"]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Mantenimiento predictivo', '["Conocimiento"]'::jsonb, 4, 2),

('MO-C9', 'Cuestionario', 'Conocimiento', '4', '19', 'Si la IA te sugiere una ruta de reparto más corta pero sabes que hay una calle cerrada, ¿qué deberías hacer?', 'Multiple Choice (una respuesta)', '["Seguir la sugerencia de IA sin pensar", "Ignorar siempre a la IA", "Combinar tu conocimiento del terreno con la sugerencia y ajustar la ruta", "Apagar el sistema para que no vuelva a sugerir rutas"]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Combinar tu conocimiento del terreno con la sugerencia y ajustar la ruta', '["Conocimiento"]'::jsonb, 4, 2),

('MO-C10', 'Cuestionario', 'Conocimiento', '4', '19', '¿Cuál de estas prácticas es más segura al usar IA generativa (ChatGPT u otras) con información de la empresa?', 'Multiple Choice (una respuesta)', '["Copiar y pegar contratos o datos de clientes sin permiso", "Solo compartir información que ya es pública o está autorizada para uso externo", "Subir fotos de credenciales de empleados", "Compartir listas completas de precios confidenciales"]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Solo compartir información que ya es pública o está autorizada para uso externo', '["Conocimiento", "Aplicación"]'::jsonb, 4, 2),

('MO-C11', 'Cuestionario', 'Conocimiento', '4', '19', 'Desde el punto de vista de la estrategia de la empresa, ¿por qué es importante que miembros de operaciones entiendan usos básicos de IA?', 'Multiple Choice (una respuesta)', '["Porque así pueden hacer el trabajo de los directores", "Para contribuir a mejoras en seguridad, calidad y servicio que afectan resultados globales", "Solo para poder reemplazar a otros compañeros", "Para eliminar todos los procedimientos escritos"]', 'MX/LATAM', '8.333333', null, '{"Correcta": 100, "Incorrecta": 0}', now(), 'Para contribuir a mejoras en seguridad, calidad y servicio que afectan resultados globales', '["Aplicación", "Conocimiento", "Estrategia"]'::jsonb, 4, 2),

